#! /usr/bin/env node 
//(shabang) bolte ha is oper wali line ko, ye line show karti ha k hamari file ya data online b available ha
//npx likh k run karte hain

import inquirer from "inquirer";

const answer = await inquirer.prompt([
  {
    message: "Enter first number",
    type: "number",
    name: "firstnumber",
  },
  { message: "Enter second number", type: "number", name: "secondnumber" },
  {
    message: "Select one of the operators to perform operation",
    type: "list",
    name: "operator",
    choices: ["Addition", "Subtraction", "Multiplication", "Division", 'Square root', 'Cube root'],
  },
]);
//conditional statement
if (answer.operator === "Addition") {
  console.log(answer.firstnumber + answer.secondnumber);
} else if (answer.operator === "Subtraction") {
  console.log(answer.firstnumber - answer.secondnumber);
} else if (answer.operator === "Multiplication") {
  console.log(answer.firstnumber * answer.secondnumber);
} else if (answer.operator === "Division") {
  console.log(answer.firstnumber / answer.secondnumber);
} else if (answer.operator === "Square root") {
  console.log(answer.firstnumber * answer.firstnumber , answer.secondnumber * answer.secondnumber);
} else if (answer.operator === "Cube root") {
    console.log(answer.firstnumber * answer.firstnumber * answer.firstnumber , answer.secondnumber * answer.secondnumber * answer.secondnumber);
} else {
  console.log("Please select valid operator");
}
